import React from 'react';
import styled from 'styled-components'
import SideNav, { NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import { BrowserRouter as Router, Route, withRouter, Link } from 'react-router-dom'
import '@trendmicro/react-sidenav/dist/react-sidenav.css';
import { logout } from '../../../../services/auth';

const headerStyles = {
    padding: 5
    // background: '#34c3ff',
  };

  const StyledLink = styled(Link)`
    color: #cdcdcd;
`


const StyledNavItem = styled(NavItem)`
    display: flex;
    top: 50px;
    flex-direction: row !important;
    align-items: center !important;
    justify-content: space-between !important; 
    /* background: green ; */
    ${({ active }) => active && `
       color: #fff;
  `}
`


function Sidebar() {
    const data = [
        { name: 'home', icon: 'home', route: '/home', defaultName: 'Homeee' },
        { name: 'user', icon: 'user', route: '/user', defaultName: 'Usuário' },
        { name: 'category', icon: 'tag', route: '/category', defaultName: 'Categoria' },
        { name: 'products', icon: 'tags', route: '/products', defaultName: 'Produtos' },
        { name: 'orders', icon: 'list-alt', route: '/orders', defaultName: 'Pedidos' },
        // { name: 'logout', icon: 'power-off', route: '/', defaultName: 'Logout' },
    ]
    return(
    <>
      <SideNav style={headerStyles} >
        <SideNav.Toggle />
        <SideNav.Nav defaultSelected="home">
        {  
            data.map((el, index) => (

                    <NavItem active={true} eventKey={el.name} >
                            <NavIcon>
                            <Link to={el.route} > 
                                <i className={`fa fa-fw fa-${el.icon}`} style={{ fontSize: '1.75em' }} />
                                </Link>
                            </NavIcon>
                            <NavText>  {el.defaultName} </NavText>
                    </NavItem>

            ))
        }
        <StyledNavItem eventKey="logout">
            <NavIcon>
                <i className="fa fa-fw fa-power-off" style={{ fontSize: '1.75em' }} />
            </NavIcon>
            <NavText>
                Logout
            </NavText>
        </StyledNavItem>
        </SideNav.Nav>
    </SideNav>      
    </>
    )
}






export default withRouter(Sidebar);
