import React, { Component } from 'react';
import { Table, Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import { withRouter, Link } from 'react-router-dom'

class ProductEdit extends Component {
    state = {
        error: null,
        products: [],
        response: {},
        msg: ''
    }

  async componentDidMount(props) {
    const id =  this.props.match.params('id')
            const response = await api.get(`/auth/product/${id}`);
            console.log(response.data)
            if(response) {
                this.setState({
                    products: response.data
                })
            }       
  }

  async deleteProduct(productId) {
    const id = productId;

    const res = await api.delete(`/auth/product/${id}`);

    if(res) {
      const payload = await api.get('/auth/product')

      if(payload) {
          this.setState({
              products: payload.data,
              msg: 'deletado com sucesso!'
          })
      }       
    }

  }

  render(props) {
    const { error, products} = this.state;

    if(error) {
      return (
        <div>Error: {error}</div>
      )
    } else {
      return(
        <div>
          <h3>Produtos</h3>
          {this.state.msg !== '' && <Alert variant="info">{this.state.msg} <Link to="" onClick={() => this.setState({ msg: ''})}>x</Link></Alert>}
          {this.state.response.message && <Alert variant="info">{this.state.response.message}</Alert>}
          <Table>
            <thead>
              <tr>
                <th>#ID</th>
                <th>Nome</th>
                <th>Categoria</th>
                <th>Descrição</th>
                <th>Preço</th>
                <th>Ativo</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>{product.category_id}</td>
                  <td>{product.description}</td>
                  <td>{product.price}</td>
                  <td>{product.active}</td>
                  <td>
                    <Button variant="info" onClick={() => this.props.history.push(`/products/${product.id}`)}>Edit</Button>
                    &nbsp;<Button variant="danger" onClick={() => this.deleteProduct(product.id)}>Delete</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )
    }
  }
}

export default withRouter(ProductEdit);
