import React, { Component } from 'react'
import {withRouter}  from 'react-router-dom'
import api from '../../../../services/api'
import Sidebar from '../fixedComponents/sidebar';
import logo from '../../../../assets/user.png'

class categoryEdit extends Component {
        state = {
            error: null,
            msg: '',
            category: [],
            id: '',
            title: '',
            description: '',
            active: '',
            mostraMsg: false,
            logo: ''
        }   

        handleChange = e => {
            this.setState({
              [e.target.name]: e.target.value
            })
          }

        handleSubmit = async e => {
            e.preventDefault();
            const { id }  = this.props.match.params;
            await api.put(`/auth/category/${id}/`,
            {id:this.state.id,title:this.state.title,description:this.state.description,active:this.state.active});
            this.setState({
                msg: 'atualizada com sucesso!'
            })       
        }      
        async componentDidMount() {
          const { id } = this.props.match.params;
          const response = await api.get(`/auth/category/${id}`);
          const res = await api.get('/auth/category')
          if(response) {
              this.setState({
                  id: response.data.id,
                  title: response.data.title,
                  description: response.data.description,
                  active: response.data.active,
                  logo: response.data.image
              })
              this.setState({
                  category: res.data
              })
          }      
        }
          render() {
            const { error, msg, mostraMsg, image } = this.state;
            if(error) {
                return(
                    <div>Error: {error}</div>
                )
            } else {
                return(
                <div>
                <Sidebar />
                <div className="home">
                    <div className="card s6">
                      <div className="row">
                        <h4 className="corPadrao white-text  ">Editar Categoria</h4>
                        <form className="col s6" onSubmit={this.handleSubmit}>

                          <div className="row edittCategory">
                            <div className="input-field col s3">
                              <i className="material-icons prefix">bookmarks</i>
                              <input id="account_circle" type="text" className="validate"
                                value={this.state.title}
                                onChange={this.handleChange}
                                required
                                placeholder="Nome da Categoria"
                                name="title"
                              />
                              <label className="active" id="account_circle">Nome</label>
                            </div>
                            <div className="col s2  imgs">
                              <img className="card-img-top" 
                                src={image == null ? logo : image[0].url}
                                style={{ width: '5rem', height: '5rem', borderRadius: '25rem', marginRight: '55px' }} 
                                alt={'Foto de  ' +this.state.title} 
                              />
                            </div>      
                          </div>
                          <div className="row">
                            <div className="col right s1.5 botaoCategory">
                              <button  type="submit" 
                                className="btn-flat btn-medium corPadrao right ">
                                <i className="material-icons white-text">save</i>
                              </button>
                              &nbsp;
                              <a  onClick={this.props.history.goBack}
                                className="btn-flat btn-medium corPadrao left ">
                                <i className="material-icons white-text">keyboard_arrow_left</i> 
                              </a>
                            </div>
                          </div>

                      </form>

                    </div>
                  </div>
                </div>
              </div>  
            )
        }
    }
}



export default withRouter(categoryEdit)
