import React, { Component } from 'react'
import ReactTooltip from 'react-tooltip'
import api from '../../../../services/api'

class OrderList extends Component{

  state = {
    orders: [],
    users: [],
    contagem: '',
    detalha: [],
    oo: []
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  async componentDidMount() {
    const res = await api.get('/auth/user')
    const response = await api.get('/auth/order')
    if(response) {
      this.setState({
          orders: response.data
      })
      this.setState({
        users: res.data
      })
      // this.setState({
      //   contagem: response.data
      // })  
    }
  }

  async detalhaProduto(order_id) {
    const response = await api.get(`/auth/order/${order_id}`);
      if(response) {
        this.setState({
          detalha: response.data
        })
      }
  }

  async entrega(orderId) {
    const id = orderId;
    const motivo = await api.post(`/auth/order/${id}`,{
      motivo: this.state.motivo,
      status: "DELIVERED"
    })

    if(motivo) {
      const payload = await api.get('/auth/order')
      if(payload) {
        this.setState({
            orders: payload.data
        })
      }       
    }
  }

  async deletaOrder (){
    const orderId = document.getElementById('campo').value
    const id = orderId;
    const motivo = await api.post(`/auth/order/${id}`,{
      motivo: this.state.motivo,
      status: "CANCELED"
    })

    const apaga = await api.delete(`/auth/order/${id}`);
    if(apaga && motivo) {
      const payload = await api.get('/auth/order')
      if(payload) {
        this.setState({
            orders: payload.data
        })
      }       
    }
  }

  onClickHandler(){
    this.setState({
       contagem : this.state.contagem+1
    });
  }

  render() {
    const { users, orders, detalha } = this.state;
    function Parametros(valor) {
      document.getElementById('campo').value = valor;
    }




    for (let i=0; i < orders.length; i++) {
      const element = orders[i];

      if(element.status === 'PENDING') {
        this.onClickHandler()
      }
    }

    return(
      <div className="home">
      <ReactTooltip />
        <div className="col s12"><h3>Central de pedidos</h3></div>
        <div className="col s6 left">
          <table className="highlight">

            <thead>
              <tr>
                <th>Pedido</th>
                <th>Cliente</th>
                <th>Forma Pagamento</th>
                <th>Total R$</th>
                <th>Troco R$</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>

            {orders.map( (order) => (
              order.status === 'PENDING' ?
              <tr key={order.id}>
                  <td>0{order.id}</td>
                    {users
                    .filter(users => users.id === order.user_id)
                    .map(users => (
                      <td key={users.id}>{users.name}</td>
                    ))}
                  <td>{order.status}</td>
                  <td>{(order.change).toLocaleString('pt-br', {style: 'currency', currency: 'BRL'})}</td>
                  <td>{(order.change).toLocaleString('pt-br', {style: 'currency', currency: 'BRL'})}</td>
                  <td><ReactTooltip />
                    <a 
                      onClick={() => this.detalhaProduto(order.id)} 
                      rel="modal:open" href="#detalha" 
                      data-id={order.id} 
                      data-tip="Detalhes" 
                      data-place="left" 
                      data-effect="solid"
                    >
                    <div className="btn-flat corPadrao darken-4">
                      <i className="material-icons white-text">more</i>
                    </div>
                    </a>
                    &nbsp;
                    <a 
                      onClick={() => this.entrega(order.id)}  
                      data-tip="Entregar" 
                      data-place="bottom" 
                      data-effect="solid"
                    >
                      <div className="btn-flat corPadrao darken-4">
                        <i className="material-icons white-text">send</i>
                      </div>
                    </a>
                    &nbsp;
                    &nbsp;
                    <a 
                      onClick={() => Parametros(order.id)}
                      rel="modal:open" href="#cancela" 
                      data-id={order.id} 
                      data-tip="Cancelar Pedido" 
                      data-place="right" 
                      data-effect="solid"
                    >
                      <div className="btn-flat corPadrao darken-4">
                        <i className="material-icons white-text">cancel</i>
                      </div>
                  </a>
                </td>
              </tr>
              : '' 
            ))}

            </tbody>
          </table>
        </div>
        <div className="col s6 right">
          <div className="card ajuste">
            <p>Pedidos Aguardando Entrega!</p> 
            <br />
            <p className="bolinha">0 {this.state.orders.status === 'PENDING' ? "020" : "021"}</p>
          </div>
        </div>


        {/* detalhamento de pedido */}
        <div className="modal tamanho" id="detalha">
          <div>
              <p>Detalhes do Pedido</p>
          </div>
          <div>
            <div className="tabelinha">
            <table className="striped">
              <thead>
                <tr>
                  <th>Produto</th>
                  <th>Quantidade</th>
                  <th>Vlr Unit.</th>
                </tr>
              </thead>
              <tbody>

                {this.state.detalha == '' ? '' : 
                  detalha.products.map(d => (
                    <tr key={d.id}>
                      <td>{d.name}</td>
                      <td>{d.description}</td>
                      <td>{d.price}</td>
                    </tr>
                  ))
                }                
              </tbody>
            </table>
          </div>

          </div>
        </div>

        {/* cancelmento de pedido */}
        <div className="modal tamanho" id="cancela">
          <input id="campo" type="hidden" value="" />
          <div>
              <p>Cancelar Pedido Nº 574</p>
              <div className="input-field col s12">
                <i className="material-icons prefix">cancel</i>
                  <textarea className="validate" 
                    name="motivo"
                    value={this.state.motivo}
                    onChange={this.handleChange}
                    required
                  />
                <p className="active">Motivo do Cancelamento</p>
                <br />

                <a  onClick={() => this.deletaOrder()}
                  className="right" data-tip="Cancelar Agora" data-place="right" data-effect="solid">
                  <div className="btn-flat corPadrao darken-4">
                    <i className="material-icons white-text">cancel</i>
                  </div>
                </a>
              </div>
          </div>
        </div>

      </div>
    )
  }
}

export default OrderList
