import React, { Component } from "react";

import ReactApexChart from "react-apexcharts";

class Outro extends Component {

    constructor(props) {
        super(props);

        this.state = {
          options: {
            plotOptions: {
            bar: {
              barHeight: '100%',
              distributed: true,
              horizontal: true,
              dataLabels: {
                position: 'bottom'
              },
            }
          },
          colors: ['#33b2df', '#546E7A', '#d4526e', '#13d8aa', '#A5978B', '#2b908f', '#f9a3a4', '#90ee7e',
            '#f48024', '#69d2e7'
          ],
          dataLabels: {
            enabled: true,
            textAnchor: 'start',
            style: {
              colors: ['#000']
            },
            formatter: function (val, opt) {
              return opt.w.globals.labels[opt.dataPointIndex] + ":  " + val
            },
            offsetX: 0,
            dropShadow: {
              enabled: false
            }
          },

          stroke: {
            width: 1,
            colors: ['#fff']
          },
          xaxis: {
            categories: [
                'Pizza Doce', 
                'Pizza Salgada', 
                'Coca Lata', 
                'Vinho do Porto', 
                'Porção de Fritas', 
                'Cerveja Latao Skoll', 
                'Pizza Calabresa',
                'Sorvete', 
                'Porção de Empanados', 
                'Cerveja Skol 600'
            ],
          },
          yaxis: {
            labels: {
              show: false
            }
          },
          title: {
              text: 'Os 10 Produtos mais vendidos',
              align: 'center',
              floating: true
          },
          tooltip: {
            theme: 'dark',
            x: {
              show: false
            },
            y: {
              title: {
                formatter: function () {
                  return ''
                }
              }
            }
          }
          },
          series: [{
            data: [400, 430, 448, 470, 540, 580, 690, 1100, 1200, 1380]
          }],
        }
      }


  render() {
    return (
        <div id="chart">
            <ReactApexChart 
                options={this.state.options} 
                series={this.state.series} 
                type="bar" height="250" 
            />
      </div>

    )
  }
}

export default Outro;
