import React, { Component } from "react";
import Chart from "react-apexcharts";

import api from '../../../../services/api'

class HomeChart extends Component {

  state = {
    novos: []
  }

  // 'Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho', 'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro' ,'Dezembro'

  async componentDidMount() {
    const response = await api.get('/auth/novos')
    if(response) {
        this.setState({
            novos: response.data
        })
      console.log(this.state.novos[0]['mes'])
    }   
  }

  constructor(props) {
    super(props);

    // if(this.state.novos[0]['mes'])



    this.state.novos.map( (data) => (
        console.log(data)
    ))

    this.state.carros.map( (carro) => (
      <ItemLista key={carro.cliente}
                cliente={carro.cliente}
                modelo={carro.modelo}
                marca={carro.marca}
                renovacao={carro.renovacao ? 'SIM' : 'NAO'}
                preco={this.formataPreco(carro.preco)}
                />
    ))


    this.state = {
      novos: [],
      mes: [],
      numero: [],
      options: {
        colors: '#546E7A',
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: ['aa']
        }
      },
      series: [
        {
          name: "Usuários Novos",
          data: [30, 40, 45, 50, 49, 60, 70, 91, 50, 20, 10, 50]
        }
      ]
    };
  }

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="340"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default HomeChart;
