import React, { Component } from 'react';
import { Table, Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import { withRouter} from 'react-router-dom'
import { Row, Col, ButtonGroup } from 'reactstrap';
import ToggleButton from 'react-bootstrap/ToggleButtonGroup'

class UserList extends Component {
    state = {
        error: null,
        users: [],
        response: {},
        msg: '',
        ativo: '',
        inativo: ''
    }

  async componentDidMount() {
            const response = await api.get('/auth/user')
            console.log(response.data)
            if(response) {
                this.setState({
                    users: response.data
                })
            }   
  }

  async deleteCategory(userId) {
    const id = userId;

    const res = await api.delete(`/auth/user/${id}`);

    if(res) {
      const payload = await api.get('/auth/user')

      if(payload) {
          this.setState({
              users: payload.data,
              msg: 'deletado com sucesso!'
          })
      }       
    }

  }

  render(props) {
    const { error, users} = this.state;

    if(error) {
      return (
        <div>Error: {error}</div>
      )
    } else {
      return(
        <div>
        <Row>
          <Col sm="11">
          {this.state.msg !== '' && <Alert variant="success">{this.state.msg}</Alert>}
          {this.state.response.message && <Alert variant="success">{this.state.response.message}</Alert>}
          </Col>
        </Row>
         <Row>
          <Col sm="10">
          <h3>Usuarios</h3>
          </Col>
          <Col sm="2"><Button onClick={() => this.props.history.push(`/AddUser`)} variant="info">Novo</Button></Col> 
        </Row>


        {users.map(users => (
          <div class="card text-center border-success " style={{ width: '12rem', height: '12rem' }} key={users.id}>
            <div class="card-body">
            <p class="card-title">{users.name}</p>
              <p class="card-text">
                <img class="card-img-top" src={users.images[0].url} style={{ width: '5rem', height: '5rem' ,borderRadius: '25rem' }} alt="{users.name}" />
              </p>
              <p>


              <ButtonGroup toggle className="mt-2">
    <ToggleButton type="radio" name="radio" defaultChecked value="1">
      Active
    </ToggleButton>
    <ToggleButton type="radio" name="radio" value="2">
      Radio
    </ToggleButton>
  </ButtonGroup>



              </p>
            </div>
          </div>
        ))}
        </div>
      )
    }
  }
}

export default withRouter(UserList);
