import React, { Component } from "react";
import Chart from "react-apexcharts";

import api from '../../../../services/api'

class HomeChart extends Component {

  async componentDidMount() {
    const response = await api.get('/auth/novos')
    if(response) {
        response.data.map( (data) => (
          // this.state.options['xaxis']['categories'].push(data.mes),
          this.state.series[0].data.push(data.total),
          (data.mes === '08') ? 
          this.state.options['xaxis']['categories'].push('Outubro')
           : ''
        ))
        console.log (this.state.meses)
    }   
  }

  constructor(props) {
    super(props);


    this.state = {
      options: {
        colors: '#546E7A',
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: []
        }
      },
      series: [
        {
          name: "Usuários Novos",
          data: []
        }
      ]
    };
  }

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="340"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default HomeChart;
