import React, { Component } from 'react';
import {
  Link,
  withRouter
} from "react-router-dom";
import ReactTooltip from 'react-tooltip'

class Header extends Component {

    async componentDidMount() {
        const category = await api.get(`/auth/category/`);
                const response = await api.get('/auth/product')
                console.log(response.data)
                if(response) {
                    this.setState({
                        products: response.data
                    })
                    this.setState({
                      category: category.data
                    })
                    console.log(this.state.category)
                }       
      }
    render() {
    return (
        <div className="topo">
        <ReactTooltip place="right" type="dark" effect="solid"/>
        <p className="welcome">Seja bem-vindo: { localStorage.getItem('nome') }</p>

            <div className="online hide abre"></div>
            <div className="ononline hide abre">Online</div>

            <div className="offline hide fecha"></div>
            <div className="offonline hide fecha">Closed</div>

            <div className="menuTopo right">
                <Link to="/a" data-tip="Configurações" data-place="left" data-effect="solid"><i className="tail material-icons white-text">settings</i></Link>
                &nbsp;&nbsp;
                <Link to="#/ativar" data-tip="Ativar Sistema" data-place="right" data-effect="solid">
                    <i className="tail material-icons white-text">settings_backup_restore</i></Link>
            </div>
        </div>
    );
   }
}
export default withRouter(Header);
