import React, { Component } from 'react';
import {  Row, Form, Col, Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import { withRouter, Link } from 'react-router-dom'

class ProductEdit extends Component {
    state = {
        error: null,
        response: {},
        msg: '',

        id: '',
        name: '',
        category_id: '',
        price: '',
        description: '',
        active: ''
    }

  async componentDidMount() {
    const { id } = this.props.match.params
            const response = await api.get(`/auth/product/${id}`);
            const category = await api.get(`/auth/category`);
            if(response) {
                this.setState({
                    id: response.data.id,
                    name: response.data.name,
                    category_id: response.data.category_id,
                    price: response.data.price,
                    description: response.data.description,
                    active: response.data.active,
                })
            }      
            if (category) {
                this.setState({
                    title: category.data.title
                })
            } 
            console.log(this.state)
  }


  async deleteProduct(productId) {
    const id = productId;

    const res = await api.delete(`/auth/product/${id}`);

    if(res) {
      const payload = await api.get('/auth/product')

      if(payload) {
          this.setState({
              products: payload.data,
              msg: 'deletado com sucesso!'
          })
      }       
    }

  }

  render() {
    const { error} = this.state;

    if(error) {
      return (
        <div>Error: {error}</div>
      )
    } else {
      return(
        <div>
          <h3>Produtos</h3>
          {this.state.msg !== '' && <Alert variant="info">{this.state.msg} <Link to="" onClick={() => this.setState({ msg: ''})}>x</Link></Alert>}
          {this.state.response.message && <Alert variant="info">{this.state.response.message}</Alert>}
          <Row>
          <Col sm={6}>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="name">
                <Form.Label>Nome</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  placeholder="Nome do Produto"/>
              </Form.Group>
              <Form.Group controlId="active">
                <Form.Label>category_id</Form.Label>
                <Form.Control as="select" name="category_id" onChange={this.handleChange}>
                {  
                 this.category.map((el, index) => (    
                         <option value='1'>Ativo</option>
                     ))
                 }
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="price">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="text"
                  name="price"
                  value={this.state.price}
                  onChange={this.handleChange}
                  placeholder="Price" />
              </Form.Group>
              <Form.Group controlId="active">
                <Form.Label>active</Form.Label>
                <Form.Control as="select" name="active">
                <option value='1'>Ativo</option>
                <option value='0'>Inativo</option>
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="description">
                <Form.Label>description</Form.Label>
                <Form.Control
                  type="text"
                  name="description"
                  value={this.state.description}
                  onChange={this.handleChange}
                  placeholder="description" />
              </Form.Group>
              <Form.Group>
                <Form.Control type="hidden" name="id" value={this.state.id} />
                <Button variant="success" type="submit">Save</Button>
              </Form.Group>
            </Form>
          </Col>
        </Row>
        </div>
      )
    }
  }
}

export default withRouter(ProductEdit);
