import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";

import api from "../../services/api";

import { Form, Containeright, Containerleft} from "./styles";

class SignUp extends Component {
  state = {
    name: "",
    cpf :"",
    email: "",
    password: "",
    telephone: "",
    address: "",
    number: "",
    neighborhood: "",
    reference: "",
    error: ""
  };

  handleSignUp = async e => {
    e.preventDefault();
    const { name, cpf, email, password, telephone, address, number, neighborhood, reference } = this.state;
    if (!name || !cpf || !email || !password || !telephone || !address || !number || !neighborhood || !reference) {
      this.setState({ error: "Preencha todos os dados para se cadastrar" });
    } else {
      try {
        await api.post("/user", { name, cpf, email, password, telephone, address, number, neighborhood, reference });
        this.props.history.push("/app");
      } catch (err) {
        console.log(err);
        this.setState({ error: "Ocorreu um erro ao registrar." });
      }
    }
  };

  render() {
    return (
      <Containeright>
        <Form onSubmit={this.handleSignUp}>
          {this.state.error && <p>{this.state.error}</p>}
          <input
            type="text"
            placeholder="Nome Completo"
            onChange={e => this.setState({ name: e.target.value })}
          />
          <input
            type="text"
            placeholder="CPF"
            onChange={e => this.setState({ cpf: e.target.value })}
          />
          <input
            type="email"
            placeholder="Endereço de e-mail"
            onChange={e => this.setState({ email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Senha"
            onChange={e => this.setState({ password: e.target.value })}
          />
          {/* <input
            type="text"
            placeholder="Telefone"
            onChange={e => this.setState({ telephone: e.target.value })}
            />
            <input
            type="text"
            placeholder="Endereço"
            onChange={e => this.setState({ address: e.target.value })}
            />
            <input
            type="text"
            placeholder="number"
            onChange={e => this.setState({ number: e.target.value })}
            />
            <input
            type="text"
            placeholder="Bairro"
            onChange={e => this.setState({ neighborhood: e.target.value })}
            />
            <input
            type="text"
            placeholder="Referência"
            onChange={e => this.setState({ reference: e.target.value })}
            /> */}
          <button type="submit">Cadastrar</button>
          <hr />
          <Link to="/">Logar</Link>
        </Form>
      </Containeright>

    );
  }
}

export default withRouter(SignUp);
