import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import Logo from "../../assets/logo.png";
import api from "../../services/api";
import { login,logout } from "../../services/auth";
import { Form, Container } from "./styles";
import styled from "styled-components";

const SocialLink = styled(Link)`  
  text-shadow: 1px 1px 1px rgba(255, 255, 255, 1);
  color: #0C1624;
  font-size: 26px;
  margin-left: 20px !important;
  &:hover {
    color: #d33837;
  }
`;


class SignIn extends Component {
  state = {
    email: "",
    password: "",
    error: ""
  };

  handleSignIn = async e => {
    e.preventDefault();
    const { email, password } = this.state;
    if (!email || !password) {
      this.setState({ error: "Preencha e-mail e senha para continuar!" });
    } else {
      try {
        const response = await api.post("/sessions", { email, password });
        login(response.data.token);
        this.props.history.push("/app");

      } catch (err) {
        this.setState({
          error:
            "Houve um problema com o login."
        });
      }
    }
  };

  render() {
	logout();
    return (
      <Container>
        <Form onSubmit={this.handleSignIn}>
        <img src={Logo} alt="logo" />
          {this.state.error && <p>{this.state.error}</p>}
          <input
            type="email"
            placeholder="EndereÃ§o de e-mail"
            onChange={e => this.setState({ email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Senha"
            onChange={e => this.setState({ password: e.target.value })}
          />
          <button type="submit">Entrar</button>
          <hr />
          <Link className="registro" to="/signup">Registrar</Link>
          <Link className="forgot" to="/forgot">Esqueceu a Senha?</Link>
          {/* <div className="row center-align">
            <div className="col s12">
                    <div className="btn white indigo-text install-app hide">
                      INSTALAR APP
                    </div>
                </div>
            </div> */}

        </Form>
        <SocialLink>teste</SocialLink>
      </Container>
    );
  }
}

export default withRouter(SignIn);
