import React, { Component } from 'react';
import ReactTooltip from 'react-tooltip';
import api from '../../../../services/api'

class OrderList extends Component{

  state = {
    orders: [],
    users: [],
    contagem: '',
    detalha: [],
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  async componentDidMount() {
    const res = await api.get('/auth/user')
    const response = await api.get('/auth/order')
    if(response) {
      this.setState({
          orders: response.data,
          contagem: response.data.length
      })
      this.setState({
        users: res.data
      })
    }
  }

  async detalhaProduto(order_id) {
    const response = await api.get(`/auth/order/${order_id}`);
      if(response) {
        this.setState({
          detalha: response.data
        })
      }
      console.log(this.state.detalha)
  }

  async deletaOrder (orderId){
    const id = orderId;
    const motivo = await api.post(`/auth/motivo/${id}`,{
      motivo: this.state.motivo,
      order_id: id
    })

    const apaga = await api.delete(`/auth/order/${id}`);
    if(apaga && motivo) {
      const payload = await api.get('/auth/order')
      if(payload) {
        this.setState({
            orders: payload.data
        })
      }       
    }
  }

  render() {
    const { users, orders, detalha } = this.state;

    return(
      <div className="home">
        <ReactTooltip place="right" type="dark" effect="solid"/>

        <div className="col s12"><h3>Central de pedidos</h3></div>
        <div className="col s6 left">
          <table className="highlight">
            <thead>
              <tr>
                <th>Pedido</th>
                <th>Cliente</th>
                <th>Forma Pagamento</th>
                <th>Total R$</th>
                <th>Troco R$</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>

            {orders.map( (order) => (
              <tr key={order.id}>
                  <td>{order.id}</td>
                    {users
                    .filter(users => users.id === order.user_id)
                    .map(users => (
                      <td key={users.id}>{users.name}</td>
                    ))}
                  <td>{order.status}</td>
                  <td>{(order.change).toLocaleString('pt-br', {style: 'currency', currency: 'BRL'})}</td>
                  <td>{(order.change).toLocaleString('pt-br', {style: 'currency', currency: 'BRL'})}</td>
                  <td>
                  <a onClick={() => this.detalhaProduto(order.id)} 
                    rel="modal:open" href="#detalha" data-id={order.id} data-tip="Detalhes" data-place="left" data-effect="solid">
                    <div className="btn-flat corPadrao darken-4">
                      <i className="material-icons white-text">more</i>
                    </div>
                  </a>
                  &nbsp;
                  <a data-tip="Entregar" data-place="bottom" data-effect="solid">
                    <div className="btn-flat corPadrao darken-4">
                      <i className="material-icons white-text">motorcycle</i>
                    </div>
                  </a>
                  &nbsp;
                  &nbsp;
                  <a rel="modal:open" href="#cancela" data-tip="Cancelar Pedido" data-place="right" data-effect="solid">
                    <div className="btn-flat corPadrao darken-4">
                      <i className="material-icons white-text">cancel</i>
                    </div>
                  </a>
                </td>
              </tr>
            ))}

            </tbody>
          </table>
        </div>
        <div className="col s6 right">
          <div className="card ajuste">
            <p>Pedidos Aguardando Entrega!</p> 
            <br />
            <p className="bolinha">0{this.state.contagem}</p>
          </div>
        </div>


        {/* detalhamento de pedido */}
        <div className="modal tamanho" id="detalha">
          <div>
              <p>Detalhes do Pedido</p>
          </div>
          <div>
            <div className="tabelinha">
            <table className="striped">
              <thead>
                <tr>
                  <th>Produto</th>
                  <th>Quantidade</th>
                  <th>Vlr Unit.</th>
                </tr>
              </thead>
              <tbody>

                {this.state.detalha == '' ? '' : 
                  detalha.products.map(d => (
                    <tr key={d.id}>
                    <td>{d.name}</td>
                    <td>{d.description}</td>
                    <td>{d.price}</td>
                  </tr>
                  ))
                }

              </tbody>
            </table>
          </div>
          <div className="right total">
            <p><b>Total</b> - R$ 60,00 </p>
          </div>
          </div>
        </div>

        {/* cancelmento de pedido */}
        <div className="modal tamanho" id="cancela">
          <div>
              <p>Cancelar Pedido Nº 574</p>
              <div className="input-field col s12">
                <i className="material-icons prefix">cancel</i>
                  <textarea className="validate" 
                    name="motivo"
                    value={this.state.motivo}
                    onChange={this.handleChange}
                    required
                  />
                <p className="active">Motivo do Cancelamento</p>
                <br />
                {/*  onClick={() => this.deletaOrder(order.id)} */}
                <a 
                  className="right" data-tip="Cancelar Agora" data-place="right" data-effect="solid">
                  <div className="btn-flat corPadrao darken-4">
                    <i className="material-icons white-text">cancel</i>
                  </div>
                </a>
              </div>
          </div>
        </div>

      </div>
    )
  }
}

export default OrderList
