import React, { Component } from 'react';
import { Table, Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import { withRouter, Link } from 'react-router-dom'

class ProductEdit extends Component {
    state = {
        error: null,
        response: {},
        msg: '',

        id: '',
        name: '',
        category_id: '',
        price: '',
        description: '',
        active: ''
    }

  async componentDidMount() {
    const { id } = this.props.match.params
            const response = await api.get(`/auth/product/${id}`);
            if(response) {
                this.setState({
                    id: response.data.id,
                    name: response.data.name,
                    price: response.data.price,
                    description: response.data.description,
                    active: response.data.active,
                })
            }       
            console.log(this.state)
  }

  async deleteProduct(productId) {
    const id = productId;

    const res = await api.delete(`/auth/product/${id}`);

    if(res) {
      const payload = await api.get('/auth/product')

      if(payload) {
          this.setState({
              products: payload.data,
              msg: 'deletado com sucesso!'
          })
      }       
    }

  }

  render(props) {
    const { error, products} = this.state;

    if(error) {
      return (
        <div>Error: {error}</div>
      )
    } else {
      return(
        <div>
          <h3>Produtos</h3>
          {this.state.msg !== '' && <Alert variant="info">{this.state.msg} <Link to="" onClick={() => this.setState({ msg: ''})}>x</Link></Alert>}
          {this.state.response.message && <Alert variant="info">{this.state.response.message}</Alert>}
          <Table>
            <thead>
              <tr>
                <th>#ID</th>
                <th>Nome</th>
                <th>Categoria</th>
                <th>Descrição</th>
                <th>Preço</th>
                <th>Ativo</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
                <tr key={this.state.id}>
                  <td>{this.state.id}</td>
                  <td>{this.state.name}</td>
                  <td>{this.state.category_id}</td>
                  <td>{this.state.description}</td>
                  <td>{this.state.price}</td>
                  <td>{this.state.active}</td>
                  <td>
                    <Button variant="info" onClick={() => this.props.history.push(`/products/${this.product.id}`)}>Edit</Button>
                    &nbsp;<Button variant="danger" onClick={() => this.deleteProduct(this.product.id)}>Delete</Button>
                  </td>
                </tr>

            </tbody>
          </Table>
        </div>
      )
    }
  }
}

export default withRouter(ProductEdit);
