import React, { Component } from 'react';
import { Row, Form, Col, Button, Alert } from 'react-bootstrap';
import api  from '../../../../services/api'
import { CategoryContainer } from '../../styles';
import Sidebar from '../fixedComponents/sidebar'

class AddCategory extends Component {
    state = {
      title: '',
      description: '',
      active: '',
      msg: ''
    }

    async componentDidMount() {
      const response = await api.get('/auth/category')
      console.log(response.data)
      if(response) {
          this.setState({
              category: response.data
          })
      }   
    }

    handleChange = e => {
        this.setState({
          [e.target.name]: e.target.value
        })
      }

  handleSubmit = async e => {
    e.preventDefault();
    await api.post('/auth/category',{
      name:this.state.name,
      description:this.state.description,
      active:this.state.active
    })
    if (api) {
        this.setState({
            name:'',
            description:'',
            active:'',
            msg: 'Cadastrada com Sucesso!'
        })  
    }

  }

  render() {
    const { category } = this.state;
    return(
      <CategoryContainer>
      <Sidebar />
      <div>
      <Row>
      <Col sm="11">
      {this.state.msg !== '' && <Alert variant="success">{this.state.msg}</Alert>}
      </Col>
    </Row>
    <h3>Cadastro de Produto</h3>
    <Row>
    <Col sm={11}>
    <Form onSubmit={this.handleSubmit}>
        <Row>  
            <Col xs="4">

                <Form.Group controlId="name">
                    <Form.Label>Nome da Categoria</Form.Label>
                    <Form.Control
                    type="text"
                    name="name"
                    required
                    value={this.state.name}
                    onChange={this.handleChange}
                    placeholder="Nome da Categoria"/>
                </Form.Group>
            </Col>
        </Row> 
        <Row>  
        <Col xs="4">
              <Form.Group controlId="active">
                <Form.Label>Ativa ?</Form.Label>
                <Form.Control as="select" required name="active" onChange={this.handleChange}>
                <option>* Selecione</option>
                <option value='1' onChange={this.handleChange}>Ativo</option>
                <option value='0' onChange={this.handleChange}>Inativo</option>
                </Form.Control>
              </Form.Group>
        </Col>
        </Row>    
        <Row>  
            <Col xs="4">
              <Form.Group controlId="description">
                <Form.Label>Descrição</Form.Label>
                <Form.Control
                  type="text"
                  required
                  name="description"
                  value={this.state.description}
                  onChange={this.handleChange}
                  placeholder="Descrição da Categoria" />
              </Form.Group>
            </Col>
        </Row>
              <Form.Group>
                <Button variant="success" type="submit">Save</Button>&nbsp;&nbsp;
                <Button onClick={this.props.history.goBack} variant="warning">Voltar</Button>
              </Form.Group>
            </Form>
          </Col>
        </Row>
      </div>
      </CategoryContainer>
    )
  }
}

export default AddCategory;
