import React, { Component } from 'react'
import {withRouter, Link}  from 'react-router-dom'
import { CategoryContainer } from '../../styles';
import {  Row, Form, Col, Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import Sidebar from '../fixedComponents/sidebar';

class categoryEdit extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
       }
        state = {
            error: null,
            msg: '',
            category: [],

            id: '',
            name: '',
            description: '',
            active: ''
        }   

        handleChange = e => {
            this.setState({
              [e.target.name]: e.target.value
            })
          }

        handleSubmit = async e => {
            e.preventDefault();
            const { id }  = this.props.match.params;
            await api.put(`/auth/category/${id}/`,
            {id:this.state.id,name:this.state.name,description:this.state.description,active:this.state.active});
            this.props.history.push('/category');
        }

      async componentDidMount() {
                const { id } = this.props.match.params;
                const response = await api.get(`/auth/category/${id}`);
                const res = await api.get('/auth/category')
                if(response) {
                    this.setState({
                        id: response.data.id,
                        name: response.data.name,
                        description: response.data.description,
                        active: response.data.active,
                    })
                    this.setState({
                        category: res.data
                    })
                }      
                console.log(this.state)
      }

    render() {
            const { error, category} = this.state;
        if(error) {
            return(
                 <div>Error: <Alert variant="info">{error}</Alert></div>
             )
        } else {
            return(
                <CategoryContainer>
        <Sidebar />
        <div>
          <h3>Editar Produto</h3>
          {this.state.msg !== '' && <Alert variant="info">{this.state.msg}</Alert>}
          <Row>
          <Col sm={6}>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="name">
                <Form.Label>Nome</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  placeholder="Nome do Produto"/>
              </Form.Group>

              <Form.Group controlId="active">
                <Form.Label>category_id</Form.Label>
                <Form.Control as="select" name="category_id" onChange={this.handleChange}>
                {  
                    category.map((el, index) => (    
                        <option key={el.id} value={el.id}>{el.title}</option>
                     ))
                 }
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="price">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="text"
                  name="price"
                  value={this.state.price}
                  onChange={this.handleChange}
                  placeholder="Price" />
              </Form.Group>
              <Form.Group controlId="active">
                <Form.Label>Ativo ?</Form.Label>
                <Form.Control as="select" name="active" onChange={this.handleChange}>
                <option value='1'>Ativo</option>
                <option value='0'>Inativo</option>
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="description">
                <Form.Label>description</Form.Label>
                <Form.Control
                  type="text"
                  name="description"
                  value={this.state.description}
                  onChange={this.handleChange}
                  placeholder="description" />
              </Form.Group>
              <Form.Group>
                <Form.Control type="hidden" name="id" value={this.state.id} />
                <Button variant="success" type="submit">Save</Button>
              </Form.Group>
            </Form>
          </Col>
        </Row>
        </div>
        </CategoryContainer>
            )
        }
    }
}



export default withRouter(categoryEdit)
