import React, { Component } from 'react';
import { Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import { withRouter} from 'react-router-dom'
import { Row, Col, ButtonGroup } from 'reactstrap';

const styles = {
  button: {
    padding: 5,
    background: '#C71585',
    width: '3rem', 
    height: '3rem' ,
    borderRadius: '25rem',
    border: '1px solid #C71585',
  },
  card: {
    border: '1px solid #C71585',
    width: '12rem', 
    height: '12rem'
  },
  btn: {
    border: '1px solid #C71585',
  }
};

class UserList extends Component {
  state = {
      error: null,
      users: [],
      response: {},
      msg: '',
      ativo: '',
      inativo: ''
  }

  async componentDidMount() {
    const response = await api.get('/auth/user')
    console.log(response.data)
    if(response) {
        this.setState({
            users: response.data
        })
    }   
  }

  async deleteCategory(userId) {
    const id = userId;
    const res = await api.delete(`/auth/user/${id}`);
    if(res) {
      const payload = await api.get('/auth/user')
      if(payload) {
          this.setState({
              users: payload.data,
              msg: 'deletado com sucesso!'
          })
      }       
    }
  }

  render(props) {
    const { error, users} = this.state;
    if(error) {
      return (
        <div>Error: {error}</div>
      )
    } else {
      return(
        <div>
        <Row>
          <Col sm="11">
            {this.state.msg !== '' && <Alert variant="success">{this.state.msg}</Alert>}
            {this.state.response.message && <Alert variant="success">{this.state.response.message}</Alert>}
          </Col>
        </Row>
         <Row>
            <Col sm="10">
              <h3>Usuarios</h3>
            </Col>
            <Col sm="2">
                <Button onClick={() => this.props.history.push(`/AddUser`)} className="button" 
                  style={styles.button}
                 ><b>+</b>
                </Button>
            </Col> 
        </Row>
        <Row>
        {users.map((users) => (
          <Col sm="2">
          <div className="card text-center " style={styles.card} key={users.id}>
            <div className="card-body">
            <p className="card-title">{users.name}</p>
              <p className="card-text">
                <img className="card-img-top" src={users.images[0] === null ? 'users.images[0].url' : "users.images[0].url"} style={{ width: '5rem', height: '5rem', borderRadius: '25rem' }} alt="{users.name}" />
              </p>
              <ButtonGroup aria-label="Basic example">
                <Button variant="btn  btn-sm" onClick={() => this.props.history.push(`/user/${users.id}`)} style={styles.btn}>Editar</Button>
                  &nbsp;
                <Button variant="btn  btn-sm" onClick={() => this.deleteCategory(users.id)} style={styles.btn}>Deletar</Button>
              </ButtonGroup>
            </div>
          </div>
          </Col>
        ))}
        </Row>
        </div>
      )
    }
  }
}

export default withRouter(UserList);
