import React, { Component } from 'react';
import { withRouter} from 'react-router-dom'

import swal from 'sweetalert';
import api from '../../../../services/api'

import logo from '../../../../assets/user.png'

const styles = {
  button: {
    background: '#C71585',
    width: '3rem', 
    height: '3rem' ,
    borderRadius: '25rem',
    border: '1px solid #C71585',
    margin: '0 0 0.5em'
  },
  card: {
    border: '1px solid #C71585',
    width: '10rem', 
    height: '10rem',
    margin: '0 0 0.5em',
  },
  btn: {
    border: '1px solid #C71585',
  },
  hr: {
    padding: '10px 0 0.10px'
  }
};

class UserList extends Component {
  state = {
      error: null,
      users: [],
      response: {},
      msg: '',
      ativo: '',
      inativo: ''
  }

  async componentDidMount() {
    const response = await api.get('/auth/user')
    console.log(response.data)
    if(response) {
        this.setState({
            users: response.data
        })
    }   
  }

  async vay(userId) {
    const id = userId;
    const res = await api.delete(`/auth/user/${id}`);
    if(res) {
      const payload = await api.get('/auth/user')
      if(payload) {
          this.setState({
              users: payload.data
          })
      }       
    }
  }

  deleteUser(UserId, userName) {
    swal({
      title: "Você tem certeza?",
      text: "Esse usuário será deletado!", userName,
      icon: "warning",
      buttons: true,
      dangerMode: true,
    }).then((willDelete) => {
      if (willDelete) {
        this.vay(UserId)
      } else {
        swal("Usuário Não será Deletado");
      }
    });
  }

  render(props) {
    const { error, users} = this.state;
    if(error) {
      return (
        <div>Error: {error}</div>
      )
    } else {
      return(
        <div className="home">

          <div class="row">
            <div className="col right">
              <a onClick={() => this.props.history.push(`/AddUser`)} 
              class="btn-floating btn-medium waves-effect waves-light corPadrao right ">
                <i class="material-icons">add</i>
              </a>
            </div>
          </div>

          {users.map((users) => (
            <div class="col  cardUsuario">
              <div class="card">
                <div class="card-image">
                  <img className="card-img-top" 
                    src={users.images[0] == null ? logo : users.images[0].url}  
                    alt={'Foto de  '+users.name} 
                  />
                  <span class="card-title black-text">{users.name}</span>
                  <a  onClick={() => this.deleteUser(users.id, users.name)}
                    class="btn-floating halfway-fab waves-effect waves-light right corPadrao">
                    <i class="material-icons">delete</i>
                  </a>
                  &nbsp;
                  <a onClick={() => this.props.history.push(`/user/${users.id}`)}
                      class="btn-floating btn-tail halfway-fab waves-effect left waves-light corPadrao">
                    <i class="material-icons">edit</i>
                  </a>
                </div>
                <div class="card-content"></div>
              </div>
            </div>
          ))}

        </div>
      )
    }
  }
}

export default withRouter(UserList);
