import React, { Component } from 'react'
import { Link } from "react-router-dom";

import { Form, Container } from "./styles";

class ForgotVolta extends Component {
    state = {
        token: '',
        password: '',
        password1: '',
        error: '',
        email: ''
    }

    handleSubmit = async e => {
        e.preventDefault();
        if(this.state.password === this.state.password1){
            const token = this.props.match.params.id;
            this.setState({ 
                error: token
            })
        } else {
            this.setState({
                error: 'Senhas Não Conferem'
            })
        }
    }

    render() {
        return(
            <Container>
                <Form onSubmit={this.handleSubmit}>
                {this.state.error && <p>{this.state.error}</p>}
                <label>Deseja Trocar senha para o Usuario 
                    <p>{this.state.email}</p>
                </label>
                    <input
                        type="text"
                        placeholder="Token Recebido"
                        required
                        onChange={e => this.setState({ token: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Nova Senha"
                        required
                        onChange={e => this.setState({ password: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Repetir Senha"
                        required
                        onChange={e => this.setState({ password1: e.target.value })}
                    />
                    <button type="submit">Cadastrar Nova Senha!</button>
                        <hr />
                    <Link className="forgot" to="/">Login</Link>
                </Form>
            </Container>
        )
    }
}

export default ForgotVolta
