import React, { Component } from 'react';
import {  Row, Form, Col, Button, Alert } from 'react-bootstrap';
import api from '../../../../services/api'
import { withRouter, Link } from 'react-router-dom'

class ProductEdit extends Component {

    state = {
        error: null,
        response: {},
        msg: '',
        category: [],
        defaultSelected: '',

        id: '',
        name: '',
        category_id: '',
        price: '',
        description: '',
        active: ''
    }



    handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
          [name]: value
        })
      }

      handleSubmit(event) {
        event.preventDefault();
        this.props.onFormSubmit(this.state);
        this.setState(this.initialState);
      }

  async componentDidMount() {
            const { id } = this.props.match.params;
            const response = await api.get(`/auth/product/${id}`);
            const res = await api.get('/auth/category')
            if(response, res) {
                this.setState({
                    id: response.data.id,
                    name: response.data.name,
                    category_id: response.data.category_id,
                    price: response.data.price,
                    description: response.data.description,
                    active: response.data.active,
                    category: res.data
                })
            }      
            console.log(this.state)
  }

  render() {
    const { error, category, category_id} = this.state;



    if(error) {
      return (
        <div>Error: <Alert variant="info">{error}</Alert></div>
      )
    } else {
      return(
        <div>
          <h3>Editar Produto</h3>
          {this.state.msg !== '' && <Alert variant="info">{this.state.msg} <Link to="" onClick={() => this.setState({ msg: ''})}>x</Link></Alert>}
          <Row>
          <Col sm={6}>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="name">
                <Form.Label>Nome</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  placeholder="Nome do Produto"/>
              </Form.Group>

              <Form.Group controlId="active">
                <Form.Label>category_id</Form.Label>
                <Form.Control as="select" name="category_id" onChange={this.handleChange}>
                {  
                    category.map((el, index) => (    
                     setState({
                         if(category_id = el.id){

                         }
                     }),
                         <option key={el.id}  defaultValue={this.state.defaultSelected} value={el.id}>{el.title}</option>
                     ))
                 }
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="price">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="text"
                  name="price"
                  value={this.state.price}
                  onChange={this.handleChange}
                  placeholder="Price" />
              </Form.Group>
              <Form.Group controlId="active">
                <Form.Label>active</Form.Label>
                <Form.Control as="select" name="active" onChange={this.handleChange}>
                <option value='1'>Ativo</option>
                <option value='0'>Inativo</option>
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="description">
                <Form.Label>description</Form.Label>
                <Form.Control
                  type="text"
                  name="description"
                  value={this.state.description}
                  onChange={this.handleChange}
                  placeholder="description" />
              </Form.Group>
              <Form.Group>
                <Form.Control type="hidden" name="id" value={this.state.id} />
                <Button variant="success" type="submit">Save</Button>
              </Form.Group>
            </Form>
          </Col>
        </Row>
        </div>
      )
    }
  }
}

export default withRouter(ProductEdit);
