import React from 'react';
import { Row, Form, Col, Button, Alert } from 'react-bootstrap';
import api  from '../../../../services/api'
import ProductContainer from '../../styles';
import Sidebar from '../fixedComponents/sidebar'

class AddProduct extends React.Component {
    state = {
      name: '',
      price: '',
      description: '',
      active: '',
      category_id: '',
      category: [],
      msg: ''
    }

    async componentDidMount() {
      const response = await api.get('/auth/category')
      console.log(response.data)
      if(response) {
          this.setState({
              category: response.data
          })
      }   
    }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit = async e => {
    e.preventDefault();
    await api.post('/auth/product',{
      name:this.state.name,
      price:this.state.price,
      description:this.state.description,
      active:this.state.active,
      category_id:this.state.category_id})
  }

  render() {
    const { msg, category} = this.state;
    return(
      <ProductContainer>
      <Sidebar />
      <div>
      <Row>
      <Col sm="11">
      {this.state.msg !== '' && <Alert variant="success">{this.state.msg}</Alert>}
      </Col>
    </Row>

        <Row>
          <Col sm={6}>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="name">
                <Form.Label>Nome do Produto</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  placeholder="Nome do Produto"/>
              </Form.Group>
              <Form.Group controlId="price">
                <Form.Label>Preço do Produto</Form.Label>
                <Form.Control
                  type="number"
                  name="price"
                  value={this.state.price}
                  onChange={this.handleChange}
                  placeholder="Preço R$ ..." />
              </Form.Group>
              <Form.Group controlId="description">
                <Form.Label>Descrição do Produto</Form.Label>
                <Form.Control
                  type="text"
                  name="description"
                  value={this.state.description}
                  onChange={this.handleChange}
                  placeholder="Descrição Breve" />
              </Form.Group>
              <Form.Group controlId="active">
                <Form.Label>Ativo ?</Form.Label>
                <Form.Control
                  type="text"
                  name="active"
                  value={this.state.active}
                  onChange={this.handleChange}
                  placeholder="Produto Ativo ?" />
              </Form.Group>
              <Form.Group controlId="category_id">
                <Form.Label>Categoria</Form.Label>
                <Form.Control as="select" name="category_id" onChange={this.handleChange}>
                {  
                    category.map((el, index) => (    
                        <option key={el.id} value={el.id}>{el.title}</option>
                     ))
                 }
                </Form.Control>
                </Form.Group>
              <Form.Group>
                <Button variant="success" type="submit">Save</Button>&nbsp;&nbsp;
                <Button onClick={this.props.history.goBack} variant="warning">Voltar</Button>
              </Form.Group>
            </Form>
          </Col>
        </Row>
      </div>
      </ProductContainer>
    )
  }
}

export default AddProduct;
